import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { CryptoState } from '../CryptoContext';
import { CoinList } from "../config/api";
import { Container, createTheme, TextField, ThemeProvider, Typography } from '@material-ui/core';

function CoinsTable() {

    const [coins, setCoins] = useState([]);
    const [loading, setLoading] = useState(false);

    const {currency} = CryptoState();
    const fetchCoins = async()=>{
       setLoading(true);
        const {data} = await axios.get(CoinList(currency));
        setCoins(data);
        setLoading(false);

    };

    console.log(coins);
    useEffect(()=>{
        fetchCoins();
    },[currency]);

    const darkTheme = createTheme({
        palette:{
            primary:{
                main:"#fff",

            },
            type:'dark',
        },
    });

    return <ThemeProvider theme={darkTheme}>
        <Container style={{textAlign: "center"}}>
            <Typography
            variant='h4' style={{fontFamily:"Montserrat", margin:18}}>
            Cryptocurrency Prices by Market Cap

            </Typography>
            <TextField label="Search For a Crypto.." variant="outlined" style={{marginBottom:20, width:"100%"}}></TextField>


        </Container>


    </ThemeProvider>
}

export default CoinsTable
